@import url("https://fonts.googleapis.com/css2?family=Yusei+Magic&display=swap");

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
  font-family: "Yusei Magic", sans-serif;
}

// scrollbar
::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-track {
  background: black;
}

::-webkit-scrollbar-thumb {
  background: $third;
  border-radius: 5px;
}

ul {
  list-style-type: none;
}

.link {
  text-decoration: none;
}

.flex {
  display: flex;
  justify-content: center;
  align-items: center;
}

.loading {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1000;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #4ba1f2;

  img {
    position: relative;
  }
}

.not-blur {
  color: red;
}

// .blur {
//   position: absolute;
//   top: 0;
//   left: 0;
//   width: 100%;
//   height: 100%;
//   background: rgba(0, 0, 0, 0.8) !important;
//   z-index: 5;
// }

.active {
  background: rgba(2, 128, 153, 0.815) !important;
}

.hide-text {
  color: transparent !important;
}

.alert {
  padding: 0.5rem;
  border-radius: 5px;
  margin-bottom: 0.5rem;
  border: 2px solid red;
  font-size: 0.9rem;
}

.fail {
  color: red;
  border-color: red;
  background: rgba(7, 4, 4, 0.7);
}

.success {
  color: green;
  border-color: green;
  background: rgba(255, 255, 255, 0.883);
}

@keyframes up-down {
  0% {
    transform: translate(0, 0);
  }

  50% {
    transform: translate(0, 20px);
  }

  100% {
    transform: translate(0, 0);
  }
}

@keyframes border-animation {
  0% {
    height: 100%;
    width: 0;
    border-bottom: 2px solid rgb(30, 46, 66);
  }

  50% {
    height: 100%;
    width: 50%;
    border-bottom: 2px solid rgb(30, 46, 66);
  }

  100% {
    height: 100%;
    width: 100%;
    border-bottom: 2px solid rgb(30, 46, 66);
  }
}

